import 'package:flutter/material.dart';import 'package:shimmer/shimmer.dart';class ShimmerList extends StatelessWidget {  @override  Widget build(BuildContext context) {    int offset = 0;    int time = 800;    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: <Widget>[        Container(          height: 25,          width: double.infinity,        ),        Expanded(            child: ListView.builder(            itemCount: 6,            itemBuilder: (BuildContext context, int index) {              offset += 5;              time = 1000 + offset;              return Padding(                  padding: EdgeInsets.symmetric(horizontal: 10),                  child: Shimmer.fromColors(                    highlightColor: Colors.grey[100],                    baseColor: Colors.grey[200],//                    baseColor: Colors.grey[300],                    child: ShimmerLayout(),                    period: Duration(milliseconds: time),                  ));            },          )),      ],    );  }}class ShimmerLayout extends StatelessWidget {  @override  Widget build(BuildContext context) {    double containerWidth = 280;    double containerHeight = 15;    return Column(      children: <Widget>[        Container(          height: 100,          child: Row(children: <Widget>[           Container(             color: Colors.grey,             width: 90,             height: 90,                      ),            SizedBox(              width: 8,            ),            Expanded(              flex: 5,              child: Container(                padding: EdgeInsets.symmetric(vertical: 10),                child: Column(                    mainAxisAlignment: MainAxisAlignment.start,                    crossAxisAlignment: CrossAxisAlignment.start,                    children: <Widget>[                      Container(                        height: containerHeight,                        width: containerWidth,                        color: Colors.grey,                      ),                      SizedBox(height: 5),                      Container(                        height: containerHeight,                        width: containerWidth,                        color: Colors.grey,                      ),                      SizedBox(height: 5),                      Container(                        height: containerHeight,                        width: containerWidth * 0.75,                        color: Colors.grey,                      )                    ]),              ),            ),            Spacer(),            Column(              children: <Widget>[                Container(                    color: Colors.grey,                ),              ],            )          ]),        ),      ],    );    return Container(      margin: EdgeInsets.symmetric(vertical: 7.5),      child: Row(        crossAxisAlignment: CrossAxisAlignment.start,        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: <Widget>[          Container(            height: 100,            width: 100,            color: Colors.grey,          ),          Spacer(),          Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: <Widget>[              Container(                height: containerHeight,                width: containerWidth,                color: Colors.grey,              ),              SizedBox(height: 5),              Container(                height: containerHeight,                width: containerWidth,                color: Colors.grey,              ),              SizedBox(height: 5),              Container(                height: containerHeight,                width: containerWidth * 0.75,                color: Colors.grey,              )            ],          )        ],      ),    );  }}